export default function checkPasswordQuality(){document.querySelectorAll(".auth__password-quality-params").forEach(t=>{const e=document.querySelector("#"+t.dataset.for);e.addEventListener("input",()=>{t.setAttribute("data-quality",function(t){let e=!1,a=!1,r=!1;for(let u of t)/[0-9]/.test(u)?e=!0:/[a-zA-Z]/.test(u)?a=!0:/[^a-zA-Z0-9]/.test(u)&&(r=!0);let u=0;return e&&u++,a&&u++,r&&u++,u}(e.value))})})}