export default function range(){document.querySelectorAll(".products-categories__range").forEach(e=>{const t=e.querySelector(".products-categories__range-element"),r=e.querySelectorAll(".products-categories__range-footer input")[0],a=e.querySelectorAll(".products-categories__range-footer input")[1];noUiSlider.create(t,{start:[Number(e.dataset.valueStart),Number(e.dataset.valueEnd)],connect:!0,range:{min:[Number(e.dataset.min)],max:[Number(e.dataset.max)]}});r&&a&&t.noUiSlider.on("update",(function(e){r.value=Math.round(e[0]),a.value=Math.round(e[1])}))})}